import React, {useState} from "react";
import './Employee.css'
import {validatePassword, passwordErrorMessage} from "../utils/PasswordValidatior"
import { login } from "../utils/LoginManager";

export const Employee = () => {
	const [userName, setUserName] = useState("");
	const [password, setPassword] = useState("");
	const [error, setError] = useState("");

	const handleSubmit = (event) => {
		event.preventDefault();
		console.log(login(userName, password));
	}

	const handlePasswordChange = (event) => {
		setPassword(event.target.value);
	}

	const handleUserNameChange = (event) => {
		setUserName(event.target.value);
	}

  return (
    <div className="container">
			
<div className="container" id="container">
	
	<div className="form-container sign-in-container">
		<form onSubmit={handleSubmit}>
			<h1>Sign in</h1>

			<span>or use your account</span>
			<input type="email" value={userName} onChange={handleUserNameChange} placeholder="Email" />
			<input type="password" value={password} onChange={handlePasswordChange} placeholder="Password" />
			<a href="#">Forgot your password?</a>
			<button>Sign In</button>
			<div style={{color: 'red', margin:10}}>{error}</div>
		</form>
	</div>
	<div className="overlay-container">
		<div className="overlay">
			<div className="overlay-panel overlay-left">
				<h1>Welcome Back!</h1>
				<p>To keep connected with us please login with your personal info</p>
				<button className="ghost" id="signIn">Sign In</button>
			</div>
			<div className="overlay-panel overlay-right">
				<h1>Hello, Friend!</h1>
				<p>Enter your personal details and start journey with us</p>
				<button className="ghost" id="signUp">Sign Up</button>
			</div>
		</div>
	</div>
</div>
    </div>
  );
}

export default Employee ;
