import React, {useState} from "react";
import './Employee.css'
import {validatePassword, passwordErrorMessage} from "../utils/PasswordValidatior"

export const Employee = () => {
	const [password, setPassword] = useState("");
	const [error, setError] = useState("");

	const handleSubmit = (event) => {
		event.preventDefault();
		validatePassword();
	}

	const validatePassworrd = () => {
  	if(!validatePassword(password)){
			setError(passwordErrorMessage);
		} else {
			setError("");
		}
	}

	const handleChange = (event) => {
		setPassword(event.target.value);
		console.log(password)
	}

  return (
    <div className="container">
			
<div className="container" id="container">
	{error !== "" && <text >{error}</text>}
	
	<div className="form-container sign-in-container">
		<form onSubmit={handleSubmit}>
			<h1>Sign in</h1>

			<span>or use your account</span>
			<input type="email" placeholder="Email" />
			<input type="password" value={password} onChange={handleChange} placeholder="Password" />
			<a href="#">Forgot your password?</a>
			<button>Sign In</button>
		</form>
	</div>
	<div className="overlay-container">
		<div className="overlay">
			<div className="overlay-panel overlay-left">
				<h1>Welcome Back!</h1>
				<p>To keep connected with us please login with your personal info</p>
				<button className="ghost" id="signIn">Sign In</button>
			</div>
			<div className="overlay-panel overlay-right">
				<h1>Hello, Friend!</h1>
				<p>Enter your personal details and start journey with us</p>
				<button className="ghost" id="signUp" onClick={() => {console.log(buttonClick())}}>Sign Up</button>
			</div>
		</div>
	</div>
</div>
    </div>
  );
}

export default Employee ;
