// SignUpComponent.js

import React, { useState } from "react";
import './SignUpComponent.css'; // Adjust the CSS file accordingly

 export const SignUp = () => {
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSignUp = async (event) => {
    event.preventDefault();

    // Assuming signUp function returns a response or handles the signup logic
    const signUpResponse = await signUp(userName, password);

    // Handle errors or success from the signUpResponse
    if (signUpResponse.error) {
      setError(signUpResponse.error);
    } else {
      // Handle success (perhaps redirect to a different page)
    }
  }

  return (
    <div className="container">
      <div className="form-container sign-up-container">
        <form onSubmit={handleSignUp}>
          <h1>Sign up</h1>
          <span>or create a new account</span>
          <input type="email" value={userName} onChange={(e) => setUserName(e.target.value)} placeholder="Email" />
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
          {/* Add additional fields for signup if needed */}
          <button>Sign Up</button>
          <div style={{ color: 'red', margin: 10 }}>{error}</div>
        </form>
      </div>
    </div>
  );
}

export default SignUp;
